import images from '../img';
import sounds from '../sounds';
import React, { useState } from 'react';

const Game = () => {
    const [score, setScore] = useState(0);
    const [selectedImage, setSelectedImage] = useState(null);
    const [gameOver, setGameOver] = useState(false);
  
    // Функция для получения четырех случайных картинок
    const getRandomImages = () => {
      const shuffledImages = [...images].sort(() => 0.5 - Math.random());
      return shuffledImages.slice(0, 4);
    };
  
    // Функция для выбора правильного звука, соответствующего картинке
    const getCorrectSound = (image) => {
      switch (image) {
        case 'alchemist':
          return sounds[1];
        case 'huntres':
          return sounds[2];
        case 'driwranger':
          return sounds[3];
        default:
          return null;
      }
    };

    const handleImageClick = (image) => {
        if (selectedImage === image) {
          setScore(score + 1);
          setSelectedImage(null);
        } else {
          setGameOver(true);
        }
      };
    
      // Обработчик нажатия на кнопку "Старт"
      const handleStartClick = () => {
        const randomImages = getRandomImages();
        const randomSound = getCorrectSound(randomImages[0]);
        setSelectedImage(randomImages[0]);
        setGameOver(false);
      };
    
      return (
        <div className="game">
          {gameOver ? (
            <div>
              <h2>Игра окончена!</h2>
              <button onClick={handleStartClick}>Начать заново</button>
            </div>
          ) : (
            <div>
              <h2>Выберите картинку, соответствующую звуку:</h2>
              <div className="images-container">
                {selectedImage && (
                  <img
                    src={require(`../img/${selectedImage}.png`).default}
                    alt={selectedImage}
                    onClick={() => handleImageClick(selectedImage)}
                  />
                )}
                {getRandomImages().map((image, index) => (
                  <img
                    key={index}
                    src={require(`../img/${image}.png`).default}
                    alt={image}
                    onClick={() => handleImageClick(image)}
                  />
                ))}
              </div>
              <div>Счет: {score}</div>
            </div>
          )}
        </div>
      );
    };
    
    export default Game;
