import React, { useState } from "react";

function Postform () {

    const [tittle, settittle] = useState ('');
    const [body, setbody ] = useState('');
    const [posts, setPosts] = useState([
        {id: 1, tittle: 'похвала 1', body: 'Description 1'},
        {id: 2, tittle: 'О том что я', body: 'Description 2'},
        {id: 3, tittle: 'лучший в мире', body: 'Description 3'},
      ]);
    
      const [posts2, setPosts2] = useState([
        {id: 1, tittle: 'пошел', body: 'Description 1'},
        {id: 2, tittle: 'нахуй', body: 'Description 2'},
        {id: 3, tittle: 'Отсюда', body: 'Description 3'},
      ]);

    const addNewPost = (e) => {
        e.preventDefault()
        const newPost = {
          id: Date.now(), tittle, body
        }
        setPosts([...posts, newPost])
        settittle('')
        setbody('')
      }

    return (
        <form>
        <input 
          value={tittle}
          onChange={e => settittle(e.target.value)}
          type="text"
          placeholder="Название"
        />
          <input 
          value={body}
          onChange={e => setbody(e.target.value)}
          type="text"
          placeholder="Описание"/>
          <button onClick={addNewPost}
          className="w-32 h-12 rounded-sm bg-white
          shadow-black shadow-sm hover:bg-[white]"> MAKE POST </button>
        </form>
    )
}

export default Postform;
