import React, { useState, useEffect } from 'react';

import alchemist from '../img/alchemist.png';
import archimag from '../img/archimag.png';
import blademaster from '../img/blademaster.png';
import bloodmage from '../img/bloodmage.png';
import demonhanter from '../img/demonhanter.png';
import drowranger from '../img/drowranger.png';
import flamebeard from '../img/flamebeard.png';
import hunters from '../img/huntres.png';

import troll from '../sounds/Лесной троль.mp3';
import Acolyte from '../sounds/Acolyte.mp3';
import Captain from '../sounds/Captain.mp3';
import Kern from '../sounds/Kern.mp3';

const images = [
  alchemist, archimag, blademaster, bloodmage, demonhanter, drowranger, flamebeard, hunters
];

const sounds = [
  troll, Acolyte, Captain, Kern
];

const getRandomImages = () => {
  const shuffledImages = images.sort(() => 0.5 - Math.random());
  return shuffledImages.slice(0, 4);
};

const getRandomSound = () => {
  const randomIndex = Math.floor(Math.random() * sounds.length);
  return sounds[randomIndex];
};

const Game = () => {
  const [start, setStart] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [score, setScore] = useState(0);
  const [selected, setSelected] = useState(null);
  const [playSound, setPlaySound] = useState(false);

  const handleStart = () => {
    setStart(true);
    setScore(0);
    setPlaySound(true);
  };

  useEffect(() => {
    if (start) {
      const selectedSound = getRandomSound();
      const selectedImageIndex = Math.floor(Math.random() * 4);
      setSelected(selectedImageIndex);
  
      const audio = new Audio(selectedSound);
      audio.play();
  
      const handleClick = () => {
        audio.pause();
        if (selectedImageIndex === selected) {
          setScore(score + 1);
        }
        setStart(false);
        setPlaySound(false);
      };
  
      // Добавьте обработчик клика для каждой картинки
      const images = document.querySelectorAll('.image');
      images.forEach((image, index) => {
        image.addEventListener('click', handleClick);
      });
  
      return () => {
        // Удалите обработчик клика после завершения игры
        images.forEach((image, index) => {
          image.removeEventListener('click', handleClick);
        });
      };
    }
  }, [start, selected, score]);


  const renderImages = () => {
    if (start) {
      const randomImages = getRandomImages();
      return randomImages.map((image, index) => (
        <img
          key={index}
          src={image}
          alt="Character"
          className={`image ${selected === index ? 'selected' : ''}`}
        />
      ));
    }
    return null;
  };

  useEffect(() => {
    if (playSound) {
      const audio = new Audio(sounds[selected]);
      audio.play();
      return () => {
        audio.pause();
      };
    }
  }, [playSound, selected]);

  return (
    <div className="App">
      {!start ? (
        <button className="start-btn" onClick={handleStart}>Start</button>
      ) : (
        <>
          <div className="images-container">
            {renderImages()}
          </div>
          <div className="score">Score: {score}</div>
        </>
      )}
    </div>
  );
};

export default Game
