import React, { useState } from 'react';

const images = ['dreadlord.png', 'drowranger.png',
'flamebreard.png',
'lich.png','medusa.png', 
'mirana.png', 'raider.png', 'sf.png'];

const sounds = ['Kern.mp3', 'Лесной троль'];

function Game () {
    const [gameStarted, setGameStarted] = useState(false);
    const [score, setScore] = useState(0);
    const [correctImage, setCorrectImage] = useState(null);
    const [selectedImage, setSelectedImage] = useState(null);

    const startGame = () => {
        setGameStarted(true);
        setScore(0);
        generateRound();
      };

      const generateRound = () => {
        const randomImages = getRandomImages(images, 4);
        const randomCorrectImage = randomImages[Math.floor(Math.random() * 4)];
        const correctImageIndex = images.indexOf(randomCorrectImage);
        const randomSound = sounds[correctImageIndex];
    
        setCorrectImage(randomCorrectImage);
        setSelectedImage(null);
        setTimeout(() => setSelectedImage(randomCorrectImage), 1000);
    }

    const getRandomImages = (arr, count) => {
        const shuffled = [...arr].sort(() => Math.random() - 0.5);
        return shuffled.slice(0, count);
      };

    const selectImage = (image) => {
        if (image === correctImage) {
          setScore(score + 1);
        }
        generateRound();
      };

    return (
        <div className="App">
      {!gameStarted ? (
        <button className="glowing-btn" onClick={startGame}>
          <span className="glowing-txt">START</span>
        </button>
      ) : (
        <>
          <div className="images-container">
            <div className="image">
              {selectedImage && (
                <img
                  src={`img/${selectedImage}`}
                  alt="Selected"
                  onClick={() => selectImage(selectedImage)}
                />
              )}
            </div>
            <div className="image">
              {images.map((image, index) => (
                <img
                  key={index}
                  src={`img/${image}`}
                  alt="Option"
                  onClick={() => selectImage(image)}
                />
              ))}
            </div>
            <div className="image">
              {/* Иконка динамика для воспроизведения звука */}
              {/* ... */}
            </div>
          </div>
          <div className="score">Score: {score}</div>
          {/* Компонент для воспроизведения звука */}
          {/* ... */}
        </>
      )}
    </div>
    )
}

export default Game;
