import { useState } from "react";
import Feedback from "./components/Freedback";
import Mainbody from "./components/Mainbody";
import PostList from "./components/PostList";

function App() {
  const [posts, setPosts] = useState([
    {id: 1, tittle: 'похвала 1', body: 'Description 1'},
    {id: 2, tittle: 'О том что я', body: 'Description 2'},
    {id: 3, tittle: 'лучший в мире', body: 'Description 3'},
  ]);

  const [posts2, setPosts2] = useState([
    {id: 1, tittle: 'пошел', body: 'Description 1'},
    {id: 2, tittle: 'нахуй', body: 'Description 2'},
    {id: 3, tittle: 'Отсюда', body: 'Description 3'},
  ]);

  const [post, setPost] = useState({tittle: '', body: ''});

  const addNewPost = (e) => {
    e.preventDefault()
    setPosts([...posts, {...post,}])
    setPost({title: '', body: '' });
  }

  return (
    <div>
        <div className="feedbackbody">
          <form>
          <input
            className="h-12 w-32" 
            value={post.tittle}
            onChange={e => setPost({...post, tittle: e.target.value})}
            type="text"
            placeholder="Твое имя"
          />
            <input 
            value={post.body}
            onChange={e => setPost({...post, body: e.target.value})}
            type="text"
            className="h-12 w-32" 
            placeholder="Отзыв"/>
            <button onClick={addNewPost}
            className="w-32 h-12 rounded-sm bg-white
            shadow-black shadow-sm hover:bg-[white]"> MAKE POST </button>
          </form>
          <PostList posts={posts} title="Отзывы от друзей"/>
        </div>
    </div>
  );
}

export default App;
