import React, { useState, useEffect } from 'react';

import alchemist from '../img/alchemist.png';
import archimag from '../img/archimag.png';
import blademaster from '../img/blademaster.png';
import bloodmage from '../img/bloodmage.png';
import demonhanter from '../img/demonhanter.png';
import drowranger from '../img/drowranger.png';
import flamebeard from '../img/flamebeard.png';
import hunters from '../img/huntres.png';
import troll from '../sounds/Лесной троль.mp3';
import Acolyte from '../sounds/Acolyte.mp3';
import Captain from '../sounds/Captain.mp3';
import Kern from '../sounds/Kern.mp3';

function Game() {
  const [images, setImages] = useState([]);
  const [score, setScore] = useState(0);
  const [selectedSound, setSelectedSound] = useState(null);
  const [isCorrect, setIsCorrect] = useState(false);

  const imageInfo = [
    { image: alchemist, sound: troll, isCorrect: true },
    { image: archimag, sound: Acolyte, isCorrect: true },
    { image: blademaster, sound: Captain, isCorrect: true },
    { image: bloodmage, sound: Kern, isCorrect: true },
  ];

  useEffect(() => {
    generateRandomSound();
  }, []);

  const generateRandomSound = () => {
    const randomIndex = Math.floor(Math.random() * imageInfo.length);
    const randomSound = imageInfo[randomIndex].sound;

    setSelectedSound(randomSound);
  };

  const generateRandomImages = () => {
    const randomImages = [];

    while (randomImages.length < 4) {
      const randomIndex = Math.floor(Math.random() * imageInfo.length);
      const randomItem = imageInfo[randomIndex];

      if (!randomImages.includes(randomItem)) {
        randomImages.push(randomItem);
      }
    }

    setImages(randomImages);
  };

  const handleImageClick = (selectedImage) => {
    if (selectedImage.sound === selectedSound) {
      setIsCorrect(true);
      setScore(1);
    } else {
      setIsCorrect(false);
      setScore(0);
    }

    generateRandomImages();
    generateRandomSound();
  };

  return (
    <div className='game'>
      <button onClick={generateRandomImages}>Показать случайные картинки</button>
      <div className='image-container'>
        {images.map((item, index) => (
          <div
            key={index}
            className={`image-wrapper ${isCorrect && item.isCorrect ? 'correct' : ''}`}
            onClick={() => handleImageClick(item)}
          >
            <img src={item.image} alt={`Image ${index}`} style={{ width: '100px' }} />
          </div>
        ))}
      </div>
      <div className='score'>Баллы: {score}</div>
    </div>
  )
}

export default Game
