import { useState } from "react";
import Feedback from "./components/Freedback";
import Mainbody from "./components/Mainbody";
import PostList from "./components/PostList";
import Postform from './components/Postform';

function App() {
  const [posts, setPosts] = useState([
    {id: 1, tittle: 'похвала 1', body: 'Description 1'},
    {id: 2, tittle: 'О том что я', body: 'Description 2'},
    {id: 3, tittle: 'лучший в мире', body: 'Description 3'},
  ]);

  function createPost(newPost) {
    setPosts([...posts, newPost])
  }

  const removePost = (post) => {
    setPost(post.filter(p => p.id !== post.id))
}


  return (
    <div>
        <div className="feedbackbody">
          <Postform create={createPost}/>
          <PostList posts={posts} title="Отзывы от друзей"/>
        </div>
    </div>
  );
}

export default App;
