import React, { useState, useEffect } from 'react';

import alchemist from '../img/alchemist.png';
import archimag from '../img/archimag.png';
import blademaster from '../img/blademaster.png';
import bloodmage from '../img/bloodmage.png';
import demonhanter from '../img/demonhanter.png';
import drowranger from '../img/drowranger.png';
import flamebeard from '../img/flamebeard.png';
import hunters from '../img/huntres.png';
import troll from '../sounds/Лесной троль.mp3';
import Acolyte from '../sounds/Acolyte.mp3';
import Captain from '../sounds/Captain.mp3';
import Kern from '../sounds/Kern.mp3';

const imageInfo = [
  { image: alchemist, sound: troll },
  { image: archimag, sound: Acolyte },
  { image: blademaster, sound: Captain },
  { image: bloodmage, sound: Kern },
];

const Game = () => {
  const [score, setScore] = useState(0);
  const [images, setImages] = useState([]);

  const playGame = () => {
    // Получаем случайные четыре картинки из массива
    const randomImages = getRandomImages(4);
    setImages(randomImages);
    
    // Воспроизводим случайный звук
    playRandomSound(randomImages);
  };

  const getRandomImages = (count) => {
    const shuffledImages = shuffleArray(imageInfo);
    return shuffledImages.slice(0, count);
  };

  const shuffleArray = (array) => {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  };

  const playRandomSound = (randomImages) => {
    const randomIndex = Math.floor(Math.random() * randomImages.length);
    const sound = randomImages[randomIndex].sound;
    const audio = new Audio(sound);
    audio.play();
  };

  const handleImageClick = (clickedImage) => {
    const correctImage = imageInfo.find(image => image.sound === clickedImage.sound);
    if (correctImage && correctImage.image === clickedImage.image) {
      setScore(score + 1);
    } else {
      setScore(0);
    }
  };

  return (
    <div>
      <h1>Web Game</h1>
      <button onClick={playGame}>Play</button>
      <p>Score: {score}</p>
      <div>
        {images.map((image, index) => (
          <img
            key={index}
            src={image.image}
            alt="Game Image"
            onClick={() => handleImageClick(image)}
          />
        ))}
      </div>
    </div>
  );
};

export default Game;
