import React, { useState, useEffect } from 'react';

import alchemist from '../img/alchemist.png';
import archimag from '../img/archimag.png';
import blademaster from '../img/blademaster.png';
import bloodmage from '../img/bloodmage.png';
import demonhanter from '../img/demonhanter.png';
import drowranger from '../img/drowranger.png';
import flamebeard from '../img/flamebeard.png';
import hunters from '../img/huntres.png';
import troll from '../sounds/Лесной троль.mp3';
import Acolyte from '../sounds/Acolyte.mp3';
import Captain from '../sounds/Captain.mp3';
import Kern from '../sounds/Kern.mp3';

function Game() {
  const [selectedImage, setSelectedImage] = useState("");
  const [selectedSound, setSelectedSound] = useState("");
  const [score, setScore] = useState(0);

  useEffect(() => {
    generateRandomCards();
  }, []);

  const imageInfo = [
    { image: alchemist, sound: troll },
    { image: archimag, sound: Acolyte },
    { image: blademaster, sound: Captain },
    { image: bloodmage, sound: Kern },
  ];

  const generateRandomCards = () => {
    const randomImageIndexes = [];
    while (randomImageIndexes.length < 4) {
      const randomIndex = Math.floor(Math.random() * imageInfo.length);
      if (!randomImageIndexes.includes(randomIndex)) {
        randomImageIndexes.push(randomIndex);
      }
    }

    const randomSoundIndex = Math.floor(Math.random() * imageInfo.length);

    setSelectedImage(imageInfo[randomSoundIndex].image);
    setSelectedSound(imageInfo[randomSoundIndex].sound);
  };

  const handleImageClick = (image) => {
    if (image === selectedImage) {
      setScore(score + 1);
    }
    generateRandomCards();
  };


  return (
<div>
      <h1>Веб-игра</h1>
      <h2>Очки: {score}</h2>
      <div>
        {imageInfo.map((info, index) => (
          <img
            key={index}
            src={info.image}
            alt=""
            className="game-image"
            onClick={() => handleImageClick(info.image)}
          />
        ))}
      </div>
      <button onClick={generateRandomCards}>Play</button>
      <p>Звук: {selectedSound}</p>
    </div>
  );
}

export default Game
