import { useState } from "react";
import Feedback from "./components/Freedback";
import Mainbody from "./components/Mainbody";
import PostList from "./components/PostList";
import Postform from './components/Postform';
import Myselect from "./components/Myselect";
import Select from "./components/Select";

function App() {
  const [posts, setPosts] = useState([
    {id: 1, tittle: 'say someth', body: 'Description 1'},
    {id: 2, tittle: 'say someth', body: 'Description 2'},
    {id: 3, tittle: 'say someth', body: 'Description 3'},
  ]);

  function createPost(newPost) {
    setPosts([...posts, newPost])
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
}

 const [selectedSort, setSelecterSort] = useState('')
 const sortPosts = (sort) => {
  setSelecterSort(sort)
  setPosts([...posts].sort((a, b) => a[sort].localeCompare(b[sort])))
 }

  return (
    <div>
        <div className="feedbackbody">
          <Postform create={createPost}/>
          <Select 
          value={selectedSort}
          onChange={sortPosts}
          defaultValue='SORTET BY' 
          option={[
            {value: 'tittle', name: 'По названию'},
            {value: 'body', name: 'По описанию'},
          ]}/>
          {posts.length === 0
          ? <p className="flex justify-center py-10 text-4xl font-mono">Нет отзывов</p>
          : <PostList remove={removePost} posts={posts} title="Отзывы от друзей"/>
          }
        </div>
    </div>
  );
}

export default App;
